  // Fun��ǜo para buscar dados da API do Jupiter para mǧltiplos mints em lote
  const fetchTokenDataFromAPI = async (mints: string[]) => {
    try {
      // Juntar todos os mints separados por v��rgula
      const query = mints.join(',');

      const response = await fetch(`/api/jupiter/tokens/v2/search?query=${query}`, {
        headers: {
          'Accept': 'application/json'
        }
      });

      if (!response.ok) {
        console.error('Erro na resposta da API:', response.status, response.statusText);
        if (response.status === 429) {
          console.warn('Rate limit atingido, tentando novamente em 5 segundos...');
          await new Promise(resolve => setTimeout(resolve, 5000));
          return await fetchTokenDataFromAPI(mints); // Retry uma vez
        }
        return new Map();
      }

      const data = await response.json();

      if (data && Array.isArray(data)) {
        // Criar um Map com os resultados indexados por mint
        const resultMap = new Map<string, { holderCount: number; mcap: number }>();
        data.forEach((item: any, index: number) => {
          if (item && 
              typeof item.holderCount === 'number' && 
              typeof item.mcap === 'number' &&
              item.holderCount >= 0 &&
              item.mcap >= 0) {
            // Usar 'id' como mint address (conforme documenta��ǜo da API)
            const mint = item.id || item.mint || mints[index];
            if (mint && typeof mint === 'string' && mint.length > 0) {
              resultMap.set(mint, {
                holderCount: item.holderCount,
                mcap: item.mcap
              });
            }
          } else {
            console.warn('Item invǭlido na resposta da API:', item);
          }
        });
        return resultMap;
      } else {
        return new Map();
      }
    } catch (error) {
      console.error(`Erro ao buscar dados para ${mints.length} mints:`, error);
      return new Map();
