// Token bucket for rate limiting (mais agressivo para paralelismo)
class TokenBucket {
  constructor(ratePerSecond, capacity) {
    this.rate = ratePerSecond;
    this.capacity = capacity;
    this.tokens = capacity;
    this.lastRefill = Date.now();
  }

  async take() {
    const now = Date.now();
    const timePassed = (now - this.lastRefill) / 1000;
    this.tokens = Math.min(this.capacity, this.tokens + timePassed * this.rate);
    this.lastRefill = now;
    if (this.tokens >= 1) {
      this.tokens -= 1;
      return true;
    }
    // Espera m��nima para nǜo sobrecarregar
    await new Promise(resolve => setTimeout(resolve, 25));
    return false;
  }
}

