async function fetchWithRetry(url, options = {}, retries = 5) {
  for (let i = 0; i < retries; i++) {
    try {
      const response = await fetch(url, options);
      if (response.status === 429) {
        const delay = Math.min(Math.pow(2, i) * 500, 3000); // Max 3s delay
        console.log(`�?� [${new Date().toISOString()}] Rate limit atingido, aguardando ${delay}ms...`);
        await new Promise(resolve => setTimeout(resolve, delay));
        continue;
      }
      if (response.status === 404) {
        console.log(`�?O [${new Date().toISOString()}] Token nǜo encontrado (404)`);
        return null; // Token not found, skip
      }
      if (!response.ok) throw new Error(`HTTP ${response.status}`);
      return await response.json();
    } catch (error) {
      if (i === retries - 1) throw error;
      console.log(`�s���? [${new Date().toISOString()}] Erro na tentativa ${i + 1}: ${error.message}, tentando novamente...`);
      await new Promise(resolve => setTimeout(resolve, 300));
    }
  }
}

