+export async function insertTransactionBatch(rows) {
+  let inserted = 0, skipped = 0, failed = 0;
+  const results = [];
+
+  if (supabase) {
+    try {
+      const { error } = await supabase.from(TABLE_NAME).insert(rows);
+      if (error) {
+        // If unique constraint exists on signature, handle partial failures
+        // Attempt per-row insert to classify inserted/skipped
+        for (const row of rows) {
+          const { error: e2 } = await supabase.from(TABLE_NAME).insert(row);
+          if (e2) {
+            skipped += 1;
+            results.push({ signature: row.signature, status: 'skipped', reason: e2.message });
+          } else {
+            inserted += 1;
+            results.push({ signature: row.signature, status: 'inserted' });
+          }
+        }
+      } else {
+        inserted = rows.length;
+        for (const row of rows) results.push({ signature: row.signature, status: 'inserted' });
+      }
+    } catch (e) {
+      failed = rows.length;
+      for (const row of rows) results.push({ signature: row.signature, status: 'failed', reason: e.message });
+    }
+  } else {
+    // Memory fallback
+    for (const row of rows) {
+      if (memoryStore.has(row.signature)) {
+        skipped += 1;
