+    if (fromTimestamp) q = q.gte('timestamp', fromTimestamp);
+    if (toTimestamp) q = q.lte('timestamp', toTimestamp);
+    q = q.order(orderBy, { ascending }).range(offset, offset + limit - 1);
+    const { data, error } = await q;
+    if (error) throw error;
+    return data || [];
+  }
+
+  // Memory fallback query
+  let arr = Array.from(memoryStore.values());
+  if (mint) arr = arr.filter(r => r.mint === mint);
+  if (signature) arr = arr.filter(r => r.signature === signature);
+  if (token_symbol) arr = arr.filter(r => r.token_symbol === token_symbol);
+  if (fromTimestamp) arr = arr.filter(r => (r.timestamp || 0) >= fromTimestamp);
+  if (toTimestamp) arr = arr.filter(r => (r.timestamp || 0) <= toTimestamp);
+  arr.sort((a, b) => (ascending ? (a[orderBy] - b[orderBy]) : (b[orderBy] - a[orderBy])));
+  return arr.slice(offset, offset + limit);
