+  // Dedup: remember last 4000 signatures
+  const seenSignatures = useRef<Set<string>>(new Set());
+  const seenQueue = useRef<string[]>([]);
+
+  const fetchTokenDataFromAPI = async (mints: string[]) => {
+    try {
+      const query = mints.join(',');
+      const response = await fetch(`/api/tokens/v2/search?query=${query}`, { headers: { 'Accept': 'application/json' } });
+      if (!response.ok) {
+        if (response.status === 429) { await new Promise(r => setTimeout(r, 5000)); return await fetchTokenDataFromAPI(mints); }
+        return new Map<string, { holderCount: number; mcap: number }>();
+      }
+      const data = await response.json();
+      const result = new Map<string, { holderCount: number; mcap: number }>();
+      if (Array.isArray(data)) {
+        data.forEach((item: any, index: number) => {
+          const mint = item?.id || item?.mint || mints[index];
+          if (!mint) return;
+          const holderCount = typeof item?.holderCount === 'number' ? item.holderCount : 0;
+          const mcap = typeof item?.mcap === 'number' ? item.mcap : 0;
+          result.set(mint, { holderCount, mcap });
+        });
+      }
+      return result;
+    } catch (e) {
+      console.error('Erro ao buscar dados Jupiter:', e);
+      return new Map<string, { holderCount: number; mcap: number }>();
+    }
+  };
+
+  const chunkArray = (array: string[], chunkSize: number): string[][] => {
+    const chunks: string[][] = [];
+    for (let i = 0; i < array.length; i += chunkSize) chunks.push(array.slice(i, i + chunkSize));
+    return chunks;
+  };
+
+  const applyFilters = (tokens: TokenData[], filterSettings: FilterSettings) => {
+    const filteredTokens = tokens.filter(token => {
+      const totalAmount = token.tableData.reduce((sum, row) => {
+        const amountStr = row.amount.split(' ')[0] || '0';
+        const num = parseFloat(amountStr);
+        return sum + (isNaN(num) ? 0 : num);
+      }, 0);
+      if (isNaN(totalAmount) || totalAmount < filterSettings.totalMin || totalAmount > filterSettings.totalMax) return false;
+      if (isNaN(token.marketCapValue) || token.marketCapValue < filterSettings.mcapMin || token.marketCapValue > filterSettings.mcapMax) return false;
+      return true;
+    });
+    return filteredTokens;
+  };
+
+  const resolveTipoFromSource = (sourceUrl?: string) => {
+    if (!sourceUrl) return 'Trade';
+    if (sourceUrl.includes('channels/958046672473194556/1241009019494072370')) return 'AG';
+    if (sourceUrl.includes('channels/1372291116853887077/1382098297891717252')) return 'Fresh';
+    if (sourceUrl.includes('channels/1372291116853887077/1382099149842812988')) return 'Dormant';
+    if (sourceUrl.includes('channels/1372291116853887077/1387731366212538390')) return 'SNS';
+    if (sourceUrl.includes('channels/1372291116853887077/1387731454577872936')) return 'AboveAVG';
+    return 'Trade';
+  };
+
+  // Live via SSE
+  useEffect(() => {
+    const es = new EventSource('/feed/sse');
+    setConnectionStatus('connecting');
+
+    es.onopen = () => setConnectionStatus('open');
+    es.onerror = () => setConnectionStatus('error');
+    es.onmessage = (e: MessageEvent) => {
+      try {
+        const evt = JSON.parse(e.data);
+        const sig: string | undefined = evt.signature; if (!sig) return;
+        if (seenSignatures.current.has(sig)) return;
+        seenSignatures.current.add(sig);
+        seenQueue.current.push(sig);
+        if (seenQueue.current.length > 4000) { const oldest = seenQueue.current.shift(); if (oldest) seenSignatures.current.delete(oldest); }
+
+        const mint: string | undefined = evt.mint; if (!mint) return;
+        setAllTokens(prev => {
+          const idx = prev.findIndex(t => t.mint === mint);
+          const tipo = resolveTipoFromSource(evt.source_url);
+          const tokenAmountValue = evt.token_amount ? parseFloat(evt.token_amount) : 0;
+          const holdersValue = typeof evt.holder_count === 'number' ? evt.holder_count : (evt.holder_count ? parseInt(evt.holder_count) : 0);
+          const marketCapValue = typeof evt.mcap === 'number' ? evt.mcap : (evt.mcap ? parseFloat(evt.mcap) : 0);
+          const ts = evt.timestamp ? (typeof evt.timestamp === 'number' ? evt.timestamp : parseInt(evt.timestamp)) : Math.floor(Date.now()/1000);
+          const newRow = {
+            hora: new Date(ts * 1000).toLocaleTimeString('pt-BR', { hour: '2-digit', minute: '2-digit' }),
+            tipo,
+            funding: Math.random() > 0.5 ? 'Long' : 'Short',
+            wallet: evt.to_user_account ? `${evt.to_user_account.substring(0,6)}...${evt.to_user_account.slice(-4)}` : 'N/A',
+            fullWallet: evt.to_user_account || '',
+            amount: !isNaN(tokenAmountValue) && tokenAmountValue > 0 ? `${tokenAmountValue.toFixed(2)} ${evt.token_symbol || ''}` : 'N/A'
+          };
+
+          if (idx >= 0) {
+            const current = prev[idx];
+            const updatedTable = [newRow, ...current.tableData].slice(0, 50);
+            const newMarketCapValue = marketCapValue || current.marketCapValue;
+            const newHoldersValue = holdersValue || current.tokenInfo.holdersValue;
+            const updated: TokenData = {
+              ...current,
+              marketCapValue: newMarketCapValue,
+              marketCap: newMarketCapValue > 0 ? `$${(newMarketCapValue / 1000).toFixed(1)}k` : 'N/A',
+              tokenInfo: {
+                ...current.tokenInfo,
+                holders: newHoldersValue.toString(),
+                holdersValue: newHoldersValue,
+                age: evt.first_pool_created_at || current.tokenInfo.age
+              },
+              verified: newHoldersValue > 50,
+              liquidity: evt.liquidity ? `$${(Number(evt.liquidity) / 1000).toFixed(1)}K` : current.liquidity,
+              website: evt.website || current.website,
+              twitter: evt.twitter || current.twitter,
+              tableData: updatedTable
+            };
+            const copy = [...prev];
+            copy[idx] = updated;
+            return copy;
+          } else {
+            const marketCapStr = marketCapValue > 0 ? `$${(marketCapValue / 1000).toFixed(1)}k` : 'N/A';
+            const chartBase = marketCapValue > 0 ? marketCapValue : 50;
+            const newToken: TokenData = {
+              id: mint || `token_${prev.length}`,
+              name: evt.token_name || 'Token Desconhecido',
+              symbol: evt.token_symbol || 'N/A',
+              icon: evt.token_icon || '🪙',
+              verified: holdersValue > 50,
+              chartData: Array.from({ length: 20 }, (_, i) => ({ value: chartBase * (1 + Math.sin(i * 0.3) * 0.1) })),
+              chartColor: Math.random() > 0.5 ? '#10b981' : '#ef4444',
+              marketCap: marketCapStr,
+              marketCapValue,
+              marketCapChange: (Math.random() - 0.5) * 10,
+              liquidity: tokenAmountValue > 0 ? `$${(tokenAmountValue / 1000).toFixed(1)}K` : 'N/A',
+              volume: 'N/A',
+              txns: 'N/A',
+              txnsRatio: 'N/A',
+              tokenInfo: {
+                holders: holdersValue.toString(),
+                holdersValue,
+                supply: tokenAmountValue > 0 ? tokenAmountValue.toLocaleString() : 'N/A',
+                age: evt.first_pool_created_at || 'N/A',
+                ageHours: (Date.now()/1000 - ts) / 3600
+              },
+              percentageChanges: { h1: (Math.random()-0.5)*5, h6: (Math.random()-0.5)*15, h24: (Math.random()-0.5)*25 },
+              tableData: [newRow],
+              tokenAmountValue,
+              mint,
+              website: evt.website,
+              twitter: evt.twitter
+            };
+            return [newToken, ...prev].slice(0, 1000);
+          }
+        });
+      } catch (err) { console.error('Erro ao processar evento SSE:', err); }
+    };
+    return () => { try { es.close(); } catch {} };
+  }, []);
+
+  // Apply filters
+  useEffect(() => {
+    const filtered = applyFilters(allTokens, filters);
+    setFilteredTokens(filtered);
+  }, [allTokens, filters]);
+
+  // Periodic market data refresh via Jupiter (batches of 100)
+  useEffect(() => {
+    const updateInterval = setInterval(async () => {
+      if (allTokens.length === 0) return;
+      try {
+        const uniqueMints = Array.from(new Set(allTokens.map(t => t.mint).filter(Boolean))) as string[];
+        if (uniqueMints.length === 0) return;
+        const mintBatches = chunkArray(uniqueMints, 100);
+        const combined = new Map<string, { holderCount: number; mcap: number }>();
+        for (let i = 0; i < mintBatches.length; i++) {
+          const batchMap = await fetchTokenDataFromAPI(mintBatches[i]);
+          batchMap.forEach((v, k) => combined.set(k, v));
+          if (i < mintBatches.length - 1) await new Promise(r => setTimeout(r, 2000));
+        }
+        if (combined.size > 0) {
+          setAllTokens(prev => prev.map(token => {
+            const apiData = combined.get(token.mint || '');
+            if (!apiData) return token;
+            const newMarketCapValue = apiData.mcap;
+            const newHoldersValue = apiData.holderCount;
+            return {
+              ...token,
+              marketCapValue: newMarketCapValue,
+              marketCap: newMarketCapValue > 0 ? `$${(newMarketCapValue / 1000).toFixed(1)}k` : 'N/A',
+              tokenInfo: {
+                ...token.tokenInfo,
+                holders: newHoldersValue.toString(),
+                holdersValue: newHoldersValue
+              },
+              verified: newHoldersValue > 50
+            };
+          }));
+        }
+      } catch (error) { console.error('Erro na atualização periódica de market data:', error); }
+    }, 20000);
+    return () => clearInterval(updateInterval);
+  }, [allTokens.length]);
+
+  const handleFiltersChange = (newFilters: FilterSettings) => { setFilters(newFilters); };
+
+  const getActiveFiltersCount = () => {
+    let count = 0;
+    if (filters.totalMin > 0) count++;
+    if (filters.totalMax < 1000000000) count++;
+    if (filters.mcapMin > 0) count++;
+    if (filters.mcapMax < 10000000) count++;
+    return count;
+  };
+  const activeFiltersCount = getActiveFiltersCount();
+
+  return (
+    <div className="bg-white rounded-lg shadow-sm border border-gray-200">
+      <div className="flex items-center justify-between py-3 px-4 bg-gray-50 border-b border-gray-200 text-sm font-medium text-gray-600">
+        <div>
+          Tokens {connectionStatus === 'connecting' && '(Conectando...)'}
+          {connectionStatus === 'error' && ' (Reconectando...)'}
+          <span className="ml-2 text-xs text-gray-500">{filteredTokens.length} de {allTokens.length} exibidos</span>
+        </div>
+        <Button
+          variant="outline"
+          size="sm"
+          onClick={() => setIsFilterOpen(true)}
+          className="flex items-center gap-2 relative"
+        >
+          Filtros
+          {activeFiltersCount > 0 && (
+            <span className="absolute -top-1 -right-1 bg-blue-600 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">{activeFiltersCount}</span>
+          )}
+        </Button>
+      </div>
+
+      <div>
+        {filteredTokens.length > 0 ? (
+          filteredTokens.map((token) => (
+            <TokenFeedRow key={token.id} token={token} />
+          ))
+        ) : (
+          <div className="p-8 text-center text-gray-500">
+            {allTokens.length === 0 ? (
+              'Aguardando eventos do backend...'
+            ) : (
+              <div>
+                <p>Nenhum token corresponde aos filtros aplicados.</p>
+                <Button variant="outline" size="sm" onClick={() => setFilters(DEFAULT_FILTERS)} className="mt-2">Limpar Filtros</Button>
+              </div>
+            )}
+          </div>
+        )}
+      </div>
+
+      <FilterPopup isOpen={isFilterOpen} onClose={() => setIsFilterOpen(false)} filters={filters} onFiltersChange={handleFiltersChange} />
+    </div>
+  );
+}
