+  const [connectionStatus, setConnectionStatus] = useState<'connecting'|'open'|'error'>('connecting');
+
+  // Dedup (últimas 4000 signatures)
+  const seenSignatures = useRef<Set<string>>(new Set());
+  const seenQueue = useRef<string[]>([]);
+
+  const resolveTipoFromSource = (sourceUrl?: string) => {
+    if (!sourceUrl) return 'Trade';
+    if (sourceUrl.includes('channels/958046672473194556/1241009019494072370')) return 'AG';
+    if (sourceUrl.includes('channels/1372291116853887077/1382098297891717252')) return 'Fresh';
