 243: function broadcastEvent(event) {
 244:   const payload = `id: ${event.id}\n` +
 245:                   `data: ${JSON.stringify(event.data)}\n\n`;
 246:   for (const res of sseClients) {
 247:     try {
 248:       res.write(payload);
 249:     } catch (e) {
 250:       // Best-effort; drop broken client
 251:       try { res.end(); } catch {}
 252:       sseClients.delete(res);
 253:     }
 254:   }
 255: }
