 336: async function fetchWithRetry(url, options = {}, retries = 1) {
 337:   for (let i = 0; i <= retries; i++) {
 338:     try {
 339:       const response = await fetch(url, options);
 340:       if (response.ok) {
 341:         return await response.json();
 342:       } else {
 343:         throw new Error(`HTTP ${response.status}: ${response.statusText}`);
 344:       }
 345:     } catch (error) {
 346:       if (i === retries) throw error;
 347:       console.log(`Retry ${i + 1} for ${url}: ${error.message}`);
 348:       await new Promise(resolve => setTimeout(resolve, 1000)); // Wait 1s before retry
 349:     }
